    def getFiles(self):
        self.fnames = QFileDialog.getOpenFileNames(filter='PDF files (*.pdf)')
        self.fnames = self.fnames[0]

    def mergeAction(self):
        '''Merge PDF's'''
        paths = self.fnames
        combinePDFs(paths)
        self.msgBox("PDF's merged")
        print(paths)
        print("PDF's merged")


